def collapse(layout, key, visible):
    return sg.pin(sg.Column(layout, key=key, visible=visible, pad=(0,0)))


def verificar_portas():
    portas = ['Auto']
    ports = serial.tools.list_ports.comports(include_links=False)
    for port in ports :
        portas.append(port[0])
    return portas

